
import { createLogger } from '../utils/logger.js';
import OllamaClient from '../core/ollama-client.js';

const logger = createLogger('test-vision.js');

async function testVision() {
  logger.info('Starting Vision Test...');

  const client = new OllamaClient();
  await client.initialize();

  // 1x1 Red Pixel JPEG base64
  const dummyImage = Buffer.from(
    '',
    'base64'
  );

  logger.info('Sending request with 1x1 pixel image...');

  try {
    const response = await client.generate({
      prompt: 'What color is this image? Reply with just the color name.',
      vision: dummyImage,
      maxTokens: 10
    });

    if (response.success) {
      logger.success(`Vision Test Passed! Response: "${response.content}"`);
    } else {
      logger.error(`Vision Test Failed: ${response.error}`);
    }
  } catch (error) {
    logger.error(`Vision Test Exception: ${error.message}`);
  }
}

testVision().catch(err => console.error(err));
